function unicodeConverter(data){
	var datas =data;
	var r = /\\u([\d\w]{4})/gi;
	datas = datas.replace(r, function (match, grp) {
		  return String.fromCharCode(parseInt(grp, 16)); } );
	var d = unescape(datas);
	return d;
}
