function hfind_duplicates(arr) {
	var len=arr.length,
	out=[],
	counts={};
	for (var i=0;i<len;i++) {
		var item = arr[i][0];
		counts[item] = counts[item] >= 1 ? counts[item] + 1 : 1;
	}
	for (var item in counts) {
		if(item!="" && counts[item] > 1)
		out.push(item);
	}
	return out;
}
