			var i, item, array = this.filterData,
			reArray = [],
			holder;
			for (i in array) {
				item = array[i];
				holder = parseInt(array[i].jsonFlightPrice.up.totalFare);
				if (min != 0 && max != 0) {
					if (typeof holder !== "undefined" && cmnBetweenSearch(holder, min, max) === true) {
						reArray.push(item);
					}
				} else {
					reArray.push(item);
				}
			}
			this.filterData = reArray;
