		//Sort by price
		sortByPrice: function (option) {
			var array = this.filterData;
			if (option == "asc") {
				array.sort(function (a, b) {
					return a.up.p - b.up.p;
				});
				this.filterData = array;
			} else if (option == "desc") {
				array.sort(function (a, b) {
					return b.up.p - a.up.p;
				});
				this.filterData = array;
			}
		},
