function stopsLangRender(value){
	var language_data = "";
	if(value!=""){
		switch(value){
			case '0':
				language_data = i18n.t("nonStop");
				break;
			case '1':
				language_data = i18n.t("oneStopKey");
				break;
			case '2':
				language_data = i18n.t("twoStopKey");
				break;
		}
	}
	return language_data;
}


