function buildLocation(key){
	var location;
	if(key[0]=='Airport'){
		location= {
				"type": key[0][0],
				"code": key[1],
				"geo": {
					"lat": key[7] || 25.197038,
					"lon": key[8] || 55.274385
				},
				"desc": key[3],
				"address": key[3],
				"cname": key[6],
				"cid": key[4],
				"cc": key[5],
				"zipCode": key[5]
		}

	}
	else if(key[0]=='Hotel'){
		location={
				"type": key[0][0],
				"code": key[1],
				"geo": {
					"lat": key[10] || 25.197038,
					"lon": key[11] || 55.274385
				},
				"desc": key[4],
				"address": key[6],
				"cname": key[6],
				"cid": key[5],
				"cc": key[9],
				"zipCode": key[9]
		}

	}
	return location;
};
