function sortObject(obj) {
    var arr = [];
    var key;
    for (key in obj) {
        if (obj.hasOwnProperty(key)) {
            arr.push([key, obj[key]]);
        }
    }
    arr.sort(function(a, b) {
        var x=(a[1]).replace(/ /g,'').toLowerCase(),
            y=(b[1]).replace(/ /g,'').toLowerCase();
        return x<y ? -1 : x>y ? 1 : 0;
    });
    return arr; // returns array
}
