function clear_form_elements(class_name) {
	  jQuery("."+class_name).find(':input').each(function() {
	    switch(this.type) {
	        case 'password':
	        case 'text':
	        case 'textarea':
	        case 'file':
	        case 'select-one':
	        case 'select-multiple':
	            jQuery(this).val('');
	            break;
	        case 'checkbox':
	        case 'radio':
	            this.checked = false;
	    }
	  });
	}

